import 'package:flutter/material.dart';import 'package:job_seekar_app/Signup/profile/body.dart';class Profile extends StatelessWidget {  final String firstName,lastName,username,password;  Profile({Key key, @required this.firstName,@required this.lastName,@required this.username,@required this.password}):super(key: key);  @override  Widget build(BuildContext context) {    return Scaffold(      body: Body(firstName: firstName,lastName: lastName,username: username,password: password,),    );  }}