import 'package:firebase_auth/firebase_auth.dart';import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:google_sign_in/google_sign_in.dart';import 'package:job_seekar_app/about/about.dart';import 'package:job_seekar_app/loginScreen.dart';import 'package:rflutter_alert/rflutter_alert.dart';import 'package:shared_preferences/shared_preferences.dart';import 'custom_page_route.dart';import 'notification/notification_setting.dart';class AppSetting extends StatefulWidget {  @override  _AppSettingState createState() => _AppSettingState();}final FirebaseAuth _auth = FirebaseAuth.instance;final GoogleSignIn _googleSignIn = GoogleSignIn();class _AppSettingState extends State<AppSetting> {  @override  Widget build(BuildContext context) {    return Scaffold(      body: Container(        padding: EdgeInsets.only(left: 16, top: 25, right: 16),        child: ListView(          children: [            Text(              "Settings",              style: TextStyle(fontSize: 25, fontWeight: FontWeight.w500),            ),            SizedBox(              height: 40,            ),            buildAccountOptionRow(context, "About"),            // buildAccountOptionRow(context, "Privacy and security"),            SizedBox(              height: 40,            ),            GestureDetector(              onTap: (){                setState(() {                  Navigator.of(context).push(CustomPageRoute(NotificationSetting()));                });              },              child: Row(                children: [                  Icon(                    Icons.notifications_none_outlined,                    color: Colors.green,                  ),                  SizedBox(                    width: 8,                  ),                  Text(                    "Notifications",                    style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold),                  ),                ],              ),            ),            Divider(              height: 15,              thickness: 2,            ),            SizedBox(              height: 5,            ),            // buildNotificationOptionRow("Opportunity", false),            SizedBox(              height: 50,            ),            Center(              child: OutlineButton(                padding: EdgeInsets.symmetric(horizontal: 40),                shape: RoundedRectangleBorder(                    borderRadius: BorderRadius.circular(20)),                onPressed: () {                  _onAlertButtonsPressed(context);                },                child: Text("SIGN OUT",                    style: TextStyle(                        fontSize: 16, letterSpacing: 2.2, color: Colors.black)),              ),            )          ],        ),      ),    );  }  Row buildNotificationOptionRow(String title, bool isActive) {    return Row(      mainAxisAlignment: MainAxisAlignment.spaceBetween,      children: [        Text(          title,          style: TextStyle(              fontSize: 18,              fontWeight: FontWeight.w500,              color: Colors.grey[600]),        ),        Transform.scale(            scale: 0.7,            child: CupertinoSwitch(              value: isActive,              onChanged: (bool val) {},            ))      ],    );  }  GestureDetector buildAccountOptionRow(BuildContext context, String title) {    return GestureDetector(      onTap: () {        Navigator.push(context, new CustomPageRoute(new About()));      },      child: Padding(        padding: const EdgeInsets.symmetric(vertical: 8.0),        child: Row(          mainAxisAlignment: MainAxisAlignment.spaceBetween,          children: [            Text(              title,              style: TextStyle(                fontSize: 18,                fontWeight: FontWeight.w500,                color: Colors.grey[600],              ),            ),            Icon(              Icons.arrow_forward_ios,              color: Colors.grey,            ),          ],        ),      ),    );  }  _onAlertButtonsPressed(context) {    Alert(      context: context,      type: AlertType.warning,      title: "Alert",      desc: "Are you sure want to sign out account ?",      buttons: [        DialogButton(          child: Text(            "NO",            style: TextStyle(color: Colors.white, fontSize: 18),          ),          onPressed: () => Navigator.pop(context),          color: Color.fromRGBO(0, 179, 134, 1.0),        ),        DialogButton(          child: Text(            "Yes",            style: TextStyle(color: Colors.white, fontSize: 18),          ),          onPressed: () async {            await _auth.signOut().then((_) async {              //try the following              _googleSignIn.signOut();              //try the following              SharedPreferences prefs =              await SharedPreferences.getInstance();              prefs.clear();              Navigator.pushReplacement(                  context, new CustomPageRoute(new LoginScreen()));            });            // Navigator.pop(context);          },          gradient: LinearGradient(colors: [            Color.fromRGBO(116, 116, 191, 1.0),            Color.fromRGBO(52, 138, 199, 1.0),          ]),        )      ],    ).show();  }}