import 'package:flutter/material.dart';import 'package:font_awesome_flutter/font_awesome_flutter.dart';import 'package:job_seekar_app/account_setting.dart';import 'package:job_seekar_app/app_setting.dart';import 'package:job_seekar_app/search_screen.dart';import 'package:motion_tab_bar/MotionTabController.dart';import 'insta_body.dart';class InstagramHome extends StatefulWidget {  @override  _InstagramHomeState createState() => _InstagramHomeState();  static const TextStyle optionStyle =      TextStyle(fontSize: 30, fontWeight: FontWeight.bold);}class _InstagramHomeState extends State<InstagramHome>    with TickerProviderStateMixin {  MotionTabController _tabController;  int _currentIndex = 0;  PageController _pageController;  @override  void initState() {    super.initState();    _pageController = PageController();    // _tabController = new MotionTabController(initialIndex: 1, vsync: this);  }  @override  void dispose() {    super.dispose();    _pageController.dispose();    // _tabController.dispose();  }  // int _currentIndex = 0;  final List<Widget> _children = [    InstaBody(),    SearchScreen(),    AccountSetting(),    AppSetting()  ];  AppBar topBar(BuildContext context) {    return new AppBar(        backgroundColor: Colors.white,        centerTitle: true,        elevation: 1.0,        automaticallyImplyLeading: false,        // leading: new Icon(Icons.camera_alt),        title: SizedBox(            height: 35.0,            child: Text(              "Paper Job",              style: TextStyle(                  color: Colors.black, fontFamily: 'Billabong', fontSize: 24.0),            )));  }  @override  Widget build(BuildContext context) {    return new Scaffold(      appBar: _currentIndex != 1 ? topBar(context) : null,      bottomNavigationBar: BottomNavigationBar(        currentIndex: _currentIndex,        showSelectedLabels: false,        showUnselectedLabels: false,        items: [          BottomNavigationBarItem(            icon: Icon(              FontAwesomeIcons.home,              color: Colors.black26,            ),            title: Text('HOME'),            activeIcon: Icon(              FontAwesomeIcons.home,              color: Colors.black,            ),          ),          BottomNavigationBarItem(            icon: Icon(              FontAwesomeIcons.search,              color: Colors.black26,            ),            title: Text('SEARCH'),            activeIcon: Icon(              FontAwesomeIcons.search,              color: Colors.black,            ),          ),          BottomNavigationBarItem(            icon: Icon(              FontAwesomeIcons.user,              color: Colors.black26,            ),            title: Text('PROFILE'),            activeIcon: Icon(              FontAwesomeIcons.user,              color: Colors.black,            ),          ),          BottomNavigationBarItem(            icon: Icon(              FontAwesomeIcons.list,              color: Colors.black26,            ),            title: Text('SETTINGS'),            activeIcon: Icon(              FontAwesomeIcons.list,              color: Colors.black,            ),          ),        ],        onTap: (index) {          setState(() {            _currentIndex = index;          });        },      ),      body: _children.elementAt(_currentIndex),    );  }  void onTabTapped(int index) {    setState(() {      _currentIndex = index;    });  }}