class Post {  String id;  int like;  int save;  String title;  String description;  String image;  int count_like;  String city;  String timestamp;  String category;  String sub_category;  Post({this.id, this.like, this.save, this.title, this.description,this.city,this.count_like, this.image,   this.timestamp,this.category,this.sub_category});  factory Post.fromJson(Map<String, dynamic> json) {    return Post(        id: json["id"],        like: json["like"],        save: json["save"],        title: json["title"],        description: json["description"],        image: json["image"],        city: json["city"],        count_like: json["count_like"],        timestamp: json["timestamp"],        category: json["category"],        sub_category: json["sub_category"]    );  }}